void setup() {
  Serial.begin(115200);
  
  // Configura los pines I2C
  Wire.begin(21, 22);

  // Inicializa la pantalla OLED
  if(!display.begin(SSD1306_SWITCHCAPVCC, SCREEN_ADDRESS)) {
    Serial.println(F("SSD1306 allocation failed"));
    for(;;);
  }

  display.display();
  delay(1000); // Pausa inicial para permitir que la pantalla se estabilice
  display.clearDisplay();
}

void loop() {
  for (int16_t x = 224; x > -224; x--) {
    display.clearDisplay();
    display.drawBitmap(x, (SCREEN_HEIGHT - 55) / 2, logo1_bmp, 224, 64, 1);
    display.display();
    delay(2);
  }

  for (int16_t x = 128; x > -128; x--) {
    display.clearDisplay();
    display.drawBitmap(x, (SCREEN_HEIGHT - 55) / 2, logo2_bmp, 128, 64, 1);
    display.display();
    delay(2);
  }
}
